import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';
import { API } from '../config/api';
import { BsTrashFill, BsCardChecklist } from 'react-icons/bs';

function Index() {
   const [todos, setTodos] = useState([]);
   const [loading, setLoading] = useState(true);
   const [todolistform, Settodolistform] = useState({
      todolist: '',
   });
   const { todolist } = todolistform;

   const onChangeForm = (e) => {
      const updateForm = { ...todolistform };
      updateForm[e.target.name] = e.target.value;
      Settodolistform(updateForm);
   };

   const addTodo = async (e) => {
      e.preventDefault();
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const data = {
         listtodo: todolist,
      };
      await API.post(`/todo`, data, config);
      fecthTodo();
      Settodolistform({ todolist: '' });
   };

   const fecthTodo = async () => {
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const res = await API.get(`/todo`, config);
      setTodos(res?.data?.data?.todo);
   };

   const deleteTodo = async (id) => {
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const res = await API.get(`/todo/${id}`, config);
   };

   const updateTodo = async (id, isDone) => {
      const config = {
         headers: {
            'Content-type': 'application/json',
         },
      };

      const data = {
         isDone: !isDone,
      };

      await API.update(`/todo/${id}`, config);
      fecthTodo();
   };

   useEffect(() => {
      fecthTodo();
   }, []);
   return (
      <div>
         <form>
            <input
               type="text"
               name="todolist"
               value={todolist}
               id=""
               onChange={(e) => onChangeForm(e)}
            />
            <button style={{}} type="submit" onClick={addTodo}>
               submit
            </button>
         </form>
         <div>
            {todos.map((todo) => (
               <div>
                  <h1>{todo.listTodo}</h1>
                  <div
                     className="clicked"
                     onClick={() => updateTodo(todo.id, todo.isDone)}
                  >
                     <BsTrashFill
                        style={{ color: 'red', width: '4rem', height: '4rem' }}
                     />
                  </div>
                  <div
                     className="clicked"
                     onClick={() => deleteTodo(todo.id, todo.isDone)}
                  >
                     <BsCardChecklist
                        style={{ color: 'red', width: '4rem', height: '4rem' }}
                     />
                  </div>
               </div>
            ))}
         </div>
      </div>
   );
}

export default Index;

