import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router';
import { API } from '../config/api';

function Index() {
   const [todos, setTodos] = useState([]);
   const [loading, setLoading] = useState(true);
   const [todolistform, Settodolistform] = useState({
      todolist: '',
   });
   const { todolist } = todolistform;

   const onChangeForm = (e) => {
      const updateForm = { ...todolistform };
      updateForm[e.target.name] = e.target.value;
      Settodolistform(updateForm);
   };

   const addTodo = async (e) => {
      e.preventDefault();
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const data = JSON.stringify({ todolist });
      await API.post(`/todo`, data, config);
      fecthTodo();
   };

   const fecthTodo = async () => {
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const res = await API.get(`/todo`, config);
      setTodos(res?.data?.data?.todo);
   };

   const deleteTodo = async (id) => {
      const config = {
         Headers: {
            'Content-Type': 'application/json',
         },
      };
      const res = await API.get(`/todo/${id}`, config);
   };

   useEffect(() => {
      fecthTodo();
   }, []);
   return (
      <div>
         <form>
            <input
               type="text"
               name="todolist"
               value={todolist}
               id=""
               onChange={(e) => onChangeForm(e)}
            />
            <button style={{}} type="submit" onClick={addTodo}>
               submit
            </button>
         </form>
         <div>
            {todos.map((todo) => (
               <h1>{todo.listTodo}</h1>
            ))}
         </div>
      </div>
   );
}

export default Index;

